'    where ri.nid_proyecto = :P37_NID_PROYECTO',
'    group by 1;',
'    ',
'    /** Existen riesgos hijos ****/',
'    IF riesgo = 1 then',
'        ',
'        /***Total riesgos **/',
'        SELECT COUNT(NID_RIESGO) into total_riesgos',
'        FROM MA_RIESGO',
'        WHERE NID_PROYECTO = :P37_NID_PROYECTO;',
'        ',
'        /** Total riesgos validados **/',
'        SELECT COUNT(NID_RIESGO) INTO total_validados',
'        FROM MA_RIESGO',
'        WHERE NID_PROYECTO = :P37_NID_PROYECTO',
'        AND NID_ESTATUS = 35;',
'        ',
'        IF :P37_NID_ESTATUS = 25 THEN',
'            IF total_riesgos = total_validados THEN',
'               --dbms_output.put_line(''true'');',
'               return true;',
'            else',
