,p_validation=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'   existe_rol NUMBER;',
'   riesgo   NUMBER;',
'begin',
'',
'   if :P39_NID_ESTATUS = 35 then',
'       select 1 into existe_rol',
'       from	 "SE_POLITICA" "SE_POLITICA"',
'       INNER JOIN "SE_ROL_POLITICA" "SE_ROL_POLITICA"',
'       ON "SE_POLITICA"."NID_POLITICA"="SE_ROL_POLITICA"."NID_POLITICA"',
'       INNER JOIN "SE_ROL" "SE_ROL"',
'       ON "SE_ROL_POLITICA"."NID_ROL"="SE_ROL"."NID_ROL"',
'       INNER JOIN "SE_ROL_USUARIO" "SE_ROL_USUARIO"',
'       ON "SE_ROL"."NID_ROL"="SE_ROL_USUARIO"."NID_ROL"',
'       INNER JOIN "SE_USUARIO" "SE_USUARIO"',
'       ON "SE_USUARIO"."NID_USUARIO"="SE_ROL_USUARIO"."NID_USUARIO"',
'       where "SE_USUARIO"."CMAIL" = UPPER(:APP_USER)',
'       and "SE_POLITICA"."NID_POLITICA" = 43',
'       group by 1;',
'       ',
'       if existe_rol = 1 then',
'          SELECT CAU.NID_RIESGO into riesgo',
'          FROM MA_RIESGO RI',
'          LEFT JOIN MA_CAUSA CAU',
'          ON RI.NID_RIESGO = CAU.NID_RIESGO',
'          WHERE RI.NID_RIESGO = :P39_NID_RIESGO;',
'          if riesgo is null then',
'              return ''No existen causas asociadas a este riesgo.'';',
'          end if;',
'       else',
'          return ''No tiene permisos para autorizar el riesgo.'';',
'       end if;',
'   end if;',
'EXCEPTION',
'   WHEN NO_DATA_FOUND THEN',
'       return ''No tiene permisos para autorizar el riesgo.'';',
'end;'))
,p_validation_type=>'FUNC_BODY_RETURNING_ERR_TEXT'
,p_always_execute=>'N'
,p_error_display_location=>'INLINE_WITH_FIELD_AND_NOTIFICATION'
);
